/* Dada a matriz (array) abaixo, criar funções para:
- listar todas atividades, o seguinte formato:
    0 - Trabalhar - 8 horas
- inserir uma nova atividade contendo a atividade e a hora
- excluir uma atividade (Array de atividade)
- alterar a hora de uma atividade
- incluir um novo valor no array de atividade:
    o novo valor deverá ser a porcentagem da hora em relação às 24 horas do dia



var atividades = [
         0           1
    ["Trabalhar"    ,8],
    ["Dormir"       ,6],
]

                     0               1
atividades[0]  ->  ["Trabalhar"     ,8]
atividades[0][0]  ->  trabalhar
atividades[0][2]  ->  undefined

*/

function listar(){
    for(var i = 0; i < atividades.length; i++){
        console.log(i, "-", atividades[i][0], "-", atividades[i][1], "horas", atividades[i][2],"%");
    }
}

var atividades = [
    
]
listar()

var prompt = require('prompt-sync');
var prompt = prompt();
var add = prompt("Informe uma atividade: ");
var addh = parseFloat(prompt("Informe a hora: "));
var porcent = addh * 4.17


atividades.push([add, addh,porcent])

listar();



var remove = parseInt(prompt("Qual item deseja remover (Escolha pelo indice)?"));
atividades.splice(remove,1);

listar();
var troca = parseInt(prompt("Informe uma atividade que deseja alterar (Escolha pelo indice): "));
var hr = parseFloat(prompt("Informe a hora que deseja atribuir: "));

atividades[troca].splice(1,1,hr);

listar();








